//SPDX-License-Identifier:MIT
pragma solidity ^0.8.0;
import "hardhat/console.sol";

contract Slot4 {}

contract Q4 {
    mapping(uint256 => address) addresses;
    bool public check;

    modifier onlyIf() {
        if (check) {
            _;
        }
    }

    function setAddress(uint256 id, address addr) public {
        addresses[id] = addr;
    }

    function getAddress(uint256 id) public view onlyIf returns (address) {
        return addresses[id];
    }

    function getAddressNoModifier(uint256 id)
        public
        view
        returns (address adr)
    {
        adr = addresses[id];
    }

    function setCheck(bool val) external returns (bool) {
        check = val;
        return check;
    }
}

contract Q9 {
    // Assume other required functionality is correctly implemented

    uint256 private constant MAX_FUND_RAISE = 100 ether;
    mapping(address => uint256) contributions;

    function contribute() external payable {
        require(address(this).balance != MAX_FUND_RAISE);
        contributions[msg.sender] += msg.value;
    }
}

contract Q12 {
    // Assume other required functionality is correctly implemented

    address admin;
    address payable pool;

    constructor(address _admin) {
        admin = _admin;
    }

    modifier onlyAdmin() {
        require(msg.sender == admin);
        _;
    }

    function setPoolAddress(address payable _pool) external onlyAdmin {
        pool = _pool;
    }

    function addLiquidity() external payable {
        pool.transfer(msg.value);
    }
}

contract Q13 {
    // Assume other required functionality is correctly implemented

    address admin;
    uint256 rewards = 10;
    modifier onlyAdmin() {
        require(msg.sender == admin);
        _;
    }

    function initialize(address _admin) external {
        require(_admin != address(0));
        admin = _admin;
    }

    function setRewards(uint256 _rewards) external onlyAdmin {
        rewards = _rewards;
    }
}

contracts/Securem/Slot3.sol.content:
//SPDX-License-Identifier:MIT
pragma solidity ^0.8.0;
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "hardhat/console.sol";

contract Slot3 {
    uint256 number1 = 10; // slot 1
    uint256 number2 = 20; // slot 2

    uint256[] public intArr;

    struct IntegerStruct {
        uint256 a;
        uint256 b;
        uint256 c;
    }

    IntegerStruct private integerStruct;

    mapping(address => uint256) userAddr;

    IERC721 erc721;

    // if searching a random location, slot returns 0 if empty
    function getValue(uint256 atSlot) external view returns (uint256 value) {
        assembly {
            value := sload(atSlot)
        }
    }

    function storeValue(uint256 atSlot, uint256 value) external {
        assembly {
            sstore(atSlot, value)
        }
    }

    // Read value of variables stored in the same slot? Bit shifting
    function getValueInBytes(uint256 atSlot)
        external
        view
        returns (bytes32 value)
    {
        assembly {
            value := sload(atSlot)
        }
    }

    function setValue(uint128 val) external {
        intArr.push(val);
    }

    function setIntegerStruct() external {
        integerStruct.a = 1;
        integerStruct.b = 2;
        integerStruct.c = 3;
    }

    function setUserAddress(address adr, uint256 val) external {
        userAddr[adr] = val;
    }

    function mappingSlotLocation(uint256 slot, address key)
        external
        pure
        returns (uint256)
    {
        return (uint256(keccak256(abi.encode(key, slot))));
    }
}

