//SPDX-License-Identifier:MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/interfaces/IERC20.sol";

contract Vesting is Ownable {
    IERC20 token;
    uint256 expiry;
    bool public isLocked;
    bool public isClaimed;
    address receiver;
    uint256 public lockedAmount;

    constructor(address _token) {
        token = IERC20(_token);
    }

    function lockFunds(uint256 _expiry, uint256 _lockedAmount)
        external
        onlyOwner
    {
        require(!isLocked, "funds alredy locked");

        expiry = _expiry;
        lockedAmount = _lockedAmount;
        isLocked = true;
        isClaimed = false;
        bool success = token.transferFrom(
            msg.sender,
            address(this),
            _lockedAmount
        );
        require(success, "deposit failed");
    }

    function withdraw() external onlyOwner {
        require(isLocked, "funds aren't locked");
        require(!isClaimed, "funds already claimed");
        require(block.timestamp > expiry, "expiry still active");
        lockedAmount = 0;
        isClaimed = true;
        bool success = token.transfer(owner(), lockedAmount);
        require(success, "withdrawal failed");
    }

    function timelockExpires() public view returns (uint256) {
        return block.timestamp - expiry;
    }

    function currentBlock() public view returns (uint256) {
        return block.timestamp;
    }

    function executeTransaction(
        address[] calldata targets,
        bytes[] calldata data
    ) external onlyOwner {
        require(targets.length == data.length, "array lengths dont match");
        uint256 i = 0;
        for (i; i < targets.length; i++) {
            (bool success, bytes memory reason) = targets[i].call(data[i]);
            require(success, string(reason));
        }
    }

    receive() external payable {}
}

contracts/RealEstate/EscrowFactory.sol.content:
//SPDX-License-Identifier:MIT
pragma solidity ^0.8.0;
import "hardhat/console.sol";
import "./Escrow.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/proxy/Clones.sol";

contract EscrowFactory is Ownable {
    address public immutable escrowImplementation;
    address[] public deployedContracts;
    uint256 fundingFee = 0.00 ether;

    event newEscrowContract(address owner, address cloneAddress);

    constructor(address _escrowImplementation) {
        escrowImplementation = _escrowImplementation;
    }

    function addEscrow(
        address _nftAdr,
        uint256 _nftID,
        uint256 _purchasePrice,
        uint256 _escrowAmount,
        address _seller,
        address _buyer,
        address _inspector,
        address _lender
    ) external payable returns (address) {
        require(msg.value >= fundingFee, "fee is less than required deposit");
        address clone = Clones.clone(escrowImplementation);
        (bool success, ) = clone.call(
            abi.encodeWithSignature(
                "initialize(address,uint256,uint256,uint256,address,address,address,address)",
                _nftAdr,
                _nftID,
                _purchasePrice,
                _escrowAmount,
                _seller,
                _buyer,
                _inspector,
                _lender
            )
        );
        require(success, "failed to clone");
        deployedContracts.push(clone);
        emit newEscrowContract(msg.sender, clone);
        return clone;
    }

    function deployedContract() public view returns (address[] memory) {
        return deployedContracts;
    }

    receive() external payable {}

    function withdraw() external onlyOwner {
        uint256 balance = address(this).balance;
        require(balance > 0, "balance is zero");
        (bool success, ) = msg.sender.call{value: balance}("");
        require(success, "failed to withdraw");
    }
}

contracts/RealEstate/Escrow.sol.content:
//SPDX-License-Identifier:MIT
pragma solidity ^0.8.0;
import "hardhat/console.sol";
import "@openzeppelin/contracts/proxy/utils/Initializable.sol";

interface IERC721 {
    function transferFrom(
        address _from,
        address _to,
        uint256 id
    ) external;
}

contract Escrow is Initializable {
    address public nftAddress;
    uint256 public nftID;
    uint256 public purchasePrice;
    uint256 public escrowAmount;
    address payable public seller;
    address payable public buyer;
    address public inspector;
    address public lender;

    modifier onlyBuyer() {
        require(msg.sender == buyer, "not called by buyer");
        _;
    }

    modifier onlySeller() {
        require(msg.sender == seller, "not called by seller");
        _;
    }

    modifier onlyInspector() {
        require(msg.sender == inspector, "not called by inspector");
        _;
    }

    modifier onlyWhiteListed() {
        require(
            msg.sender == buyer || msg.sender == seller || msg.sender == lender,
            "not whitelisted"
        );
        _;
    }

    bool public inspectionPassed;
    bool public fundsDeposited;
    mapping(address => bool) public approvals;

    function initialize(
        address _nftAddress,
        uint256 _nftID,
        uint256 _purchasePrice,
        uint256 _escrowAmount,
        address _seller,
        address _buyer,
        address _inspector,
        address _lender
    ) external {
        nftAddress = _nftAddress;
        nftID = _nftID;
        purchasePrice = _purchasePrice;
        escrowAmount = _escrowAmount;
        seller = payable(_seller);
        buyer = payable(_buyer);
        inspector = _inspector;
        lender = _lender;
    }

    // Put Under Contract (only buyer - payable escrow)
    function depositEarnest() external payable onlyBuyer {
        require(msg.value >= escrowAmount, "Insufficient deposit");
        fundsDeposited = true;
    }

    function approveSale() public onlyWhiteListed {
        approvals[msg.sender] = true;
    }

    // Finalize Sale
    // -> Require inspection status (add more items here, like appraisal)
    // -> Require sale to be authorized
    // -> Require funds to be correct amount
    // -> Transfer NFT to buyer
    // -> Transfer Funds to Seller

    function finalizeSale() public payable onlyInspector {
        uint256 balance = address(this).balance;
        require(inspectionPassed, "inspection not passed");
        require(approvals[lender] == true);
        require(approvals[buyer] == true);
        require(approvals[lender] == true);

        require(balance >= purchasePrice);

        // transfer NFT to buyer
        IERC721(nftAddress).transferFrom(address(this), buyer, nftID);
        // transfer funds to seller
        (bool success, ) = payable(seller).call{value: balance}("");
        require(success, "transfer failed");
    }

    function cancelSale() public onlyWhiteListed {
        // Cancel Sale (handle earnest deposit)
        // -> if inspection status is not approved, then refund, otherwise send to seller
        require(fundsDeposited, "funds not deposited");
        if (!inspectionPassed) {
            (bool success, ) = payable(buyer).call{
                value: address(this).balance
            }("");
            require(success);
        } else {
            (bool success, ) = payable(seller).call{
                value: address(this).balance
            }("");
            require(success);
        }
    }

    receive() external payable {}

    function getBalance() public view returns (uint256) {
        return address(this).balance;
    }

    function setInspectionStatus(bool status) public onlyInspector {
        inspectionPassed = status;
    }
}

